import React, { Component } from 'react';
import './Modal.css';
export default class EditProduct extends Component {
  constructor(props) {
    super(props);

    this.initialState = {
      ProductId: '',
      ProductName: '',
      CategoryId: '',
      SkuId: '',
      Price: '',
      Attributes: [],
    };

    this.state = this.initialState;
    this.state = { checked: false, selectedAttrs: [] };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleCheckboxChange = this.handleCheckboxChange.bind(this);
  }
  componentDidMount() {}
  componentDidUpdate() {}
  handleChange(event) {
    const name = event.target.name;
    const value = event.target.value;

    this.setState({
      [name]: value,
    });
  }

  handleSubmit(event) {
    event.preventDefault();
    this.props.onFormSubmit(this.state);
    this.setState(this.initialState);
  }
  handleCheckboxChange = (event) => {
    let newArray = [...this.state.selectedAttrs, event.target.id];
    if (event.target.checked) {
      if (this.state.selectedAttrs.includes(event.target.id)) {
        newArray = newArray.filter((attr) => attr !== event.target.id);
      }
      this.setState({
        selectedAttrs: newArray,
      });
    } else {
      newArray = newArray.filter((attr) => attr !== event.target.id);
    }

    console.log(...this.state.selectedAttrs);
  };
  onClose = (e) => {
    this.props.handleClose && this.props.handleClose(e);
  };
  render() {
    if (!this.props.showEdit) {
      return null;
    }
    return (
      <div className="mod">
        <div className="mod-content">
          <div className="mod-header">
            <h4 className="mod-title">Edit Product</h4>
          </div>
          <div className="mod-body">
            <div className="card bg-warning">
              <div className="table-responsive">
                <form onSubmit={this.handleSubmit}>
                  <table
                    className="table table-striped table-sm table-success table-bordered"
                    data-link="row"
                  >
                    <tbody>
                      <tr>
                        <td className="align-bottom font-weight-bold">
                          <label className="control-label">Product Name</label>
                        </td>
                        <td className="align-bottom">
                          <input
                            type="text"
                            name="ProductName"
                            id="ProductName"
                            onChange={this.handleChange}
                            placeholder="Product Name"
                            className="form-control"
                            defaultValue={
                              this.props.productData.ProductName || ''
                            }
                          />
                        </td>
                      </tr>
                      <tr>
                        <td className="align-bottom font-weight-bold">
                          <label className="control-label">Category</label>
                        </td>
                        <td className="align-bottom">
                          <select
                            id="CategoryId"
                            name="CategoryId"
                            className="form-control"
                            onChange={this.handleChange}
                            defaultValue={
                              this.props.productData.CategoryId || ''
                            }
                          >
                            <option value="">====Select====</option>
                            {this.props.categories.map((cat) => (
                              <option key={cat.Id} value={cat.Id}>
                                {cat.CategoryName}
                              </option>
                            ))}
                          </select>
                        </td>
                      </tr>
                      <tr>
                        <td className="align-bottom font-weight-bold">
                          <label className="control-label">SKU</label>
                        </td>
                        <td className="align-bottom">
                          <select
                            id="SkuId"
                            name="SkuId"
                            className="form-control"
                            onChange={this.handleChange}
                            defaultValue={this.props.productData.SkuId || ''}
                          >
                            <option value="">====Select====</option>
                            {this.props.skus.map((sku) => (
                              <option key={sku.Id} value={sku.Id}>
                                {sku.SkuName}
                              </option>
                            ))}
                          </select>
                        </td>
                      </tr>
                      <tr>
                        <td className="align-bottom font-weight-bold">
                          <label className="control-label">Price</label>
                        </td>
                        <td className="align-bottom">
                          <input
                            type="text"
                            name="Price"
                            id="Price"
                            className="form-control"
                            onChange={this.handleChange}
                            placeholder="Price"
                            defaultValue={this.props.productData.Price || ''}
                          />
                          <input
                            type="hidden"
                            name="Id"
                            id="Id"
                            defaultValue={
                              this.props.productData.ProductId || ''
                            }
                            onChange={this.handleChange}
                          />
                          <input
                            type="text"
                            name="Attributes"
                            id="Attributes"
                            value={this.state.selectedAttrs}
                            defaultValue={
                              this.props.productData.ProductId || ''
                            }
                            onChange={this.handleChange}
                          />
                        </td>
                      </tr>
                      <tr>
                        <td
                          colSpan="2"
                          className="align-bottom font-weight-bold"
                        >
                          <fieldset className="border p-2">
                            <legend className="w-auto">
                              Product Attributes
                            </legend>
                            {this.props.attributesData.map((attr) => (
                              <div key={attr.ProductAttributeValueId}>
                                <input
                                  type="checkbox"
                                  name={attr.ProductAttributeValueId}
                                  id={attr.ProductAttributeValueId}
                                  defaultValue={attr.ProductAttributeValueId}
                                  checked={attr.Selected}
                                  onChange={this.handleCheckboxChange}
                                />
                                &nbsp;
                                {attr.AttributeName}
                              </div>
                            ))}
                          </fieldset>
                        </td>
                      </tr>

                      <tr>
                        <td colSpan="2">
                          <button
                            className="btn btn-sm btn-primary fa-pull-right"
                            type="submit"
                          >
                            Update Product
                          </button>
                          <button
                            className="btn btn-sm btn-danger"
                            onClick={this.onClose}
                          >
                            Close
                          </button>
                        </td>
                      </tr>
                    </tbody>
                  </table>
                </form>
              </div>
            </div>
          </div>
          <div className="mod-footer"></div>
        </div>
      </div>
    );
  }
}
