import React, { Component } from 'react';
import http from './AppConfig';
import { CategoryList } from './CategoryList';
import { Button } from 'react-bootstrap';

export class ProductCategory extends Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      products: [],
      response: {},
    };
  }
  componentDidMount() {
    http
      .get('/products')
      .then((response) => response.data)
      .then(
        (result) => {
          this.setState({
            products: result,
          });
        },
        (error) => {
          this.setState({ error });
        },
      );
  }
  render() {
    const { error, products } = this.state;
    var count = 1;
    if (error) {
      return <div>Error:{error.message}</div>;
    }
    return (
      <div className="row">
        <div className="col-sm-9">
          <div class="card bg-warning">
            <table class="table table-striped table-success table-sm">
              <tr>
                <th></th>
                <th>Product Name</th>
                <th>Category</th>
                <th>Attributes</th>
                <th>Price</th>
                <th colSpan="2"></th>
              </tr>
              <tbody>
                {products.map((p) => (
                  <tr key={p.ProductId}>
                    <td>{count++}</td>
                    <td>{p.ProductName}</td>
                    <td>{p.CategoryName}</td>
                    <td>{p.Attributes}</td>
                    <td>${p.Price.toFixed(2)}</td>
                    <td>
                      <Button
                        class="btn btn-primary py-0 btn-sm"
                        onClick={() => this.getDetails(p.ProductId)}
                      >
                        Details
                      </Button>
                      |
                      <Button
                        class="btn btn-info py-0 btn-sm"
                        onClick={() => this.props.editProduct(p.ProductId)}
                      >
                        Edit
                      </Button>
                      |
                      <Button
                        class="btn btn-danger py-0 btn-sm"
                        onClick={() => this.deleteProduct(p.ProductId)}
                      >
                        Delete
                      </Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    );
  }
}
