import React, { Component } from 'react';
import NavigationSubCategoryLink from './NavigationSubCategoryLink';

class NavigationSubCategoryList extends Component {
  renderSubCategories() {
    const { selectedCategoryId, items } = this.props;
    // This is the key to filtering based on selectedCategoryId
    return (
      items
        .filter((item) => {
          // Checking all the categories in the item's categories array
          // against the selectedCategoryId passed in from props
          return item.categories.some((category) => {
            return category.id === selectedCategoryId;
          });
        })
        // After filtering what you need, map through
        // the new, shorter array and render each item
        .map((item) => {
          const { title, link, id } = item;
          return (
            <NavigationSubCategoryLink key={id} name={title} link={link} />
          );
        })
    );
  }
  render() {
    return (
      <div className="subCategoryContainer">{this.renderSubCategories()}</div>
    );
  }
}
export default NavigationSubCategoryList;
