import React, { Component } from 'react';
import AddProduct from './AddProduct';
import http from './AppConfig';
import EditProduct from './EditProduct';

const _ = require('lodash');
const CATEGORIES_ENDPOINT = '/productCategories';
const PRODUCTS_ENDPOINT = '/products';
const SKU_ENDPOINT = '/sku';
const ATTRIBUTES_ENDPOINT = '/productAttributes';
class Nav extends Component {
  constructor(props) {
    super(props);

    this.state = {
      categories: [],
      products: [],
      skus: [],
      attributes: [],
      selectedCategoryId: null,
      showEdit: false,
      showAdd: false,
      error: null,
      response: {},
      productData: {},
      isAddProduct: false,
      isEditProduct: false,
    };
    this.onFormSubmit = this.onFormSubmit.bind(this);
    this.onSelectCategory = this.onSelectCategory.bind(this);
    this.showAddModal = this.showAddModal.bind(this);
    this.showEditModal = this.showEditModal.bind(this);
    this.hideModal = this.hideModal.bind(this);
    this.editProduct = this.editProduct.bind(this);
  }
  componentDidMount() {
    http
      .get(CATEGORIES_ENDPOINT)
      .then((response) => response.data)
      .then(
        (result) => {
          this.setState({
            categories: result,
          });
        },
        (error) => {
          this.setState({ error });
        },
      );
    http
      .get(PRODUCTS_ENDPOINT)
      .then((response) => response.data)
      .then(
        (result) => {
          this.setState({
            products: result,
          });
        },
        (error) => {
          this.setState({ error });
        },
      );
    http
      .get(SKU_ENDPOINT)
      .then((response) => response.data)
      .then(
        (result) => {
          this.setState({
            skus: result,
          });
        },
        (error) => {
          this.setState({ error });
        },
      );
    http
      .get(ATTRIBUTES_ENDPOINT)
      .then((response) => response.data)
      .then(
        (result) => {
          this.setState({
            attributes: result,
          });
        },
        (error) => {
          this.setState({ error });
        },
      );
  }

  onFormSubmit(data) {
    if (this.state.isEditProduct) {
      this.setState({
        isAddProduct: false,
        isEditProduct: false,
      });
      alert(data.Id);
      alert(data.ProductName);
      http.put('/productsById/' + data.Id, data).then((result) => {
        alert(result.data);
        this.setState({
          response: result,
          isAddProduct: false,
          isEditProduct: false,
        });
      });
    } else {
      this.setState({
        isAddProduct: false,
        isEditProduct: false,
      });
      http.post('/products', data).then((result) => {
        this.setState({
          response: result,
          isAddProduct: false,
          isEditProduct: false,
        });
      });
    }
  }

  editProduct(Id) {
    http
      .get('/productsById/' + Id)
      .then((response) => response.data)
      .then(
        (result) => {
          this.setState({
            productData: result,
            isEditProduct: true,
          });
        },
        (error) => {
          this.setState({ error });
        },
      );
  }
  onSelectCategory(Id) {
    this.setState({
      selectedCategoryId: Id,
    });
  }
  showAddModal() {
    this.setState({ showAdd: true, isAddProduct: true });
  }
  showEditModal(Id) {
    this.setState({
      showEdit: true,
    });

    this.editProduct(Id);
  }

  hideModal() {
    this.setState({
      showAdd: false,
      showEdit: false,
    });
  }
  render() {
    const {
      attributes,
      skus,
      categories,
      products,
      selectedCategoryId,
      showAdd,
      showEdit,
      productData,
    } = this.state;
    const defaultCategory = _.first(categories);
    const selectedCategory =
      _.find(categories, (i) => i.Id === selectedCategoryId) || defaultCategory;
    return (
      <div>
        <div className="header"></div>
        <div className="topnav"></div>
        <br />
        <div className="row">
          <div className="col-sm-3">
            <CategoryFilter
              categories={categories}
              onSelectCategory={this.onSelectCategory}
            />
          </div>

          <div className="col-sm-9">
            <ProductList
              products={products}
              selectedCategory={selectedCategory}
              showEditModal={this.showEditModal}
            />

            <AddProduct
              showAdd={showAdd}
              handleClose={this.hideModal}
              categories={categories}
              skus={skus}
              attributes={attributes}
              products={products}
              onFormSubmit={this.onFormSubmit}
            ></AddProduct>
            <EditProduct
              showEdit={showEdit}
              handleClose={this.hideModal}
              categories={categories}
              skus={skus}
              attributes={attributes}
              products={products}
              productData={productData}
              onFormSubmit={this.onFormSubmit}
            ></EditProduct>
            <button
              className="btn btn-primary btn-sm"
              onClick={this.showAddModal}
            >
              Add Product
            </button>
          </div>
          <div className="row:after">
            <div className="footer"></div>
          </div>
        </div>
      </div>
    );
  }
}

var CategoryFilter = ({ categories, onSelectCategory }) => {
  const links = categories.map((i) => (
    <div key={i.Id}>
      <button
        className="btn btn-secondary btn-block"
        onClick={() => onSelectCategory(i.Id)}
      >
        {i.CategoryName}
      </button>
      <br />
    </div>
  ));
  return <div>{links}</div>;
};

var ProductList = ({ products, selectedCategory, showEditModal }) => {
  var count = 1;

  const currentProducts = products
    .filter((i) => i.CategoryId === selectedCategory.Id)
    .map((i) => (
      <tr key={i.ProductId}>
        <td>{count++}</td>
        <td>{i.ProductName}</td>
        <td>{i.CategoryName}</td>
        <td>{i.SkuName}</td>
        <td>{i.Price}</td>
        <td>{i.Attributes}</td>
        <td>
          <button
            className="btn btn-info py-0 btn-sm"
            onClick={() => showEditModal(i.ProductId)}
          >
            Edit
          </button>
          |<button className="btn btn-danger py-0 btn-sm">Delete</button>
        </td>
      </tr>
    ));
  return (
    <div>
      <table className="table table-striped table-sm table-success table-bordered">
        <tbody>
          <tr>
            <th></th>
            <th>Product Name</th>
            <th>Category Name</th>
            <th>SKU</th>
            <th>Price</th>
            <th>Attributes</th>
            <th></th>
          </tr>
          {currentProducts}
        </tbody>
      </table>
    </div>
  );
};

export default Nav;
