import React, { Component } from 'react';
import AddProduct from './AddProduct';
import http from './AppConfig';

const _ = require('lodash');
const CATEGORIES_ENDPOINT = '/productCategories';
const PRODUCTS_ENDPOINT = '/products';
const SKU_ENDPOINT = '/sku';
const ATTRIBUTES_ENDPOINT = '/productAttributes';
class Nav extends Component {
  constructor(props) {
    super(props);

    this.state = {
      categories: [],
      products: [],
      skus: [],
      attributes: [],
      selectedCategoryId: null,
      show: false,
      isAddProduct: false,
      error: null,
      response: {},
      productData: {},
      isEditProduct: false,
      isProductDetails: true,
    };
    this.onFormSubmit = this.onFormSubmit.bind(this);
    this.onSelectCategory = this.onSelectCategory.bind(this);
    this.showModal = this.showModal.bind(this);
    this.hideModal = this.hideModal.bind(this);
  }
  componentDidMount() {
    http
      .get(CATEGORIES_ENDPOINT)
      .then((response) => response.data)
      .then(
        (result) => {
          this.setState({
            categories: result,
          });
        },
        (error) => {
          this.setState({ error });
        },
      );
    http
      .get(PRODUCTS_ENDPOINT)
      .then((response) => response.data)
      .then(
        (result) => {
          this.setState({
            products: result,
          });
        },
        (error) => {
          this.setState({ error });
        },
      );
    http
      .get(SKU_ENDPOINT)
      .then((response) => response.data)
      .then(
        (result) => {
          this.setState({
            skus: result,
          });
        },
        (error) => {
          this.setState({ error });
        },
      );
    http
      .get(ATTRIBUTES_ENDPOINT)
      .then((response) => response.data)
      .then(
        (result) => {
          this.setState({
            attributes: result,
          });
        },
        (error) => {
          this.setState({ error });
        },
      );
  }
  onCreate() {
    this.setState({ isAddProduct: true });
    this.setState({ isProductDetails: false });
  }
  onDetails() {
    this.setState({ isProductDetails: true });
    this.setState({ isAddProduct: false });
  }

  onFormSubmit(data) {
    this.setState({ isAddProduct: true });
    this.setState({ isProductDetails: false });
    if (this.state.isEditProduct) {
      http.put('/products/' + data.Id, data).then((result) => {
        alert(result.data);
        this.setState({
          response: result,
          isAddProduct: false,
          isEditProduct: false,
        });
      });
    } else {
      http.post('/products', data).then((result) => {
        alert(result.data);
        this.setState({
          response: result,
          isAddProduct: false,
          isEditProduct: false,
        });
      });
    }
  }

  editProduct = (Id) => {
    this.setState({ isProductDetails: false });
    http.get('/products/' + Id).then(
      (result) => {
        this.setState({
          isEditProduct: true,
          isAddProduct: true,
          productData: result,
        });
      },
      (error) => {
        this.setState({ error });
      },
    );
  };
  onSelectCategory(Id) {
    this.setState({
      selectedCategoryId: Id,
    });
  }
  showModal = () => {
    this.setState({ show: true });
  };

  hideModal = () => {
    this.setState({ show: false });
  };
  render() {
    const { attributes, skus, categories, products, selectedCategoryId, show } =
      this.state;
    const defaultCategory = _.first(categories);
    const selectedCategory =
      _.find(categories, (i) => i.Id === selectedCategoryId) || defaultCategory;
    return (
      <div>
        <div className="header"></div>
        <div className="topnav"></div>
        <br />
        <div className="row">
          <div className="col-sm-3">
            <CategoryFilter
              categories={categories}
              onSelectCategory={this.onSelectCategory}
            />
          </div>

          <div className="col-sm-9">
            <ProductList
              products={products}
              selectedCategory={selectedCategory}
            />

            <AddProduct
              show={show}
              handleClose={this.hideModal}
              categories={categories}
              skus={skus}
              attributes={attributes}
              products={products}
            ></AddProduct>
            <button className="btn btn-primary btn-sm" onClick={this.showModal}>
              Add Product
            </button>
          </div>
          <div className="row:after">
            <div className="footer"></div>
          </div>
        </div>
      </div>
    );
  }
}

var CategoryFilter = ({ categories, onSelectCategory }) => {
  const links = categories.map((i) => (
    <div key={i.Id}>
      <button
        className="btn btn-secondary btn-block"
        onClick={() => onSelectCategory(i.Id)}
      >
        {i.CategoryName}
      </button>
      <br />
    </div>
  ));
  return <div>{links}</div>;
};

var ProductList = ({ products, selectedCategory }) => {
  var count = 1;

  const currentProducts = products
    .filter((i) => i.CategoryId === selectedCategory.Id)
    .map((i) => (
      <tr key={i.ProductId}>
        <td>{count++}</td>
        <td>{i.ProductName}</td>
        <td>{i.CategoryName}</td>
        <td>{i.SkuName}</td>
        <td>{i.Price}</td>
        <td>{i.Attributes}</td>
        <td>
          <button className="btn btn-info py-0 btn-sm">Edit</button>|
          <button className="btn btn-primary py-0 btn-sm">Details</button>|
          <button className="btn btn-danger py-0 btn-sm">Delete</button>
        </td>
      </tr>
    ));
  return (
    <div>
      <table className="table table-striped table-sm table-success table-bordered">
        <tbody>
          <tr>
            <th></th>
            <th>Product Name</th>
            <th>Category Name</th>
            <th>SKU</th>
            <th>Price</th>
            <th>Attributes</th>
            <th></th>
          </tr>
          {currentProducts}
        </tbody>
      </table>
    </div>
  );
};

export default Nav;
