import React, { Component } from 'react';
import { BrowserRouter as Router } from 'react-router-dom';
import Modal from 'react-modal';
import { Button } from 'react-bootstrap';
import http from './AppConfig';
import EditProduct from './EditProduct';

const _ = require('lodash');
const CATEGORIES_ENDPOINT = '/productCategories';
const PRODUCTS_ENDPOINT = '/products';

class Nav extends Component {
  constructor() {
    super();

    this.state = {
      categories: [],
      products: [],
      selectedCategoryId: null,
      openModal: false,
      setShow: false,
    };

    this.onSelectCategory = this.onSelectCategory.bind(this);
    this.editProduct = this.editProduct.bind(this);
    this.handleClose = this.handleClose.bind(this);
    this.handleShow = this.handleShow.bind(this);
  }
  componentDidMount() {
    http
      .get(CATEGORIES_ENDPOINT)
      .then((response) => response.data)
      .then(
        (result) => {
          this.setState({
            categories: result,
          });
        },
        (error) => {
          this.setState({ error });
        },
      );
    http
      .get(PRODUCTS_ENDPOINT)
      .then((response) => response.data)
      .then(
        (result) => {
          this.setState({
            products: result,
          });
        },
        (error) => {
          this.setState({ error });
        },
      );
  }
  handleClose = () => {
    this.setState({ openModal: false });
  };
  onCloseModal = () => {
    this.setState({ openModal: false });
  };
  handleShow() {
    //e.preventDefault();
    this.setState({ openModal: true });
  }
  onSelectCategory(Id) {
    this.setState({
      selectedCategoryId: Id,
    });
  }
  editProduct = (Id) => {
    alert(Id);
    this.setState({
      //selectedCategoryId: Id,
    });
  };

  render() {
    const { categories, products, selectedCategoryId, openModal } = this.state;
    const defaultCategory = _.first(categories);
    const selectedCategory =
      _.find(categories, (i) => i.Id === selectedCategoryId) || defaultCategory;
    return (
      <div className="row">
        <div className="col-sm-3">
          <CategoryFilter
            categories={categories}
            onSelectCategory={this.onSelectCategory}
          />
        </div>
        <div className="col-sm-9">
          <ProductList
            products={products}
            selectedCategory={selectedCategory}
            handleShow={this.handleShow}
            openModal={openModal}
          />
        </div>
      </div>
    );
  }
}

var CategoryFilter = ({ categories, onSelectCategory }) => {
  const links = categories.map((i) => (
    <div key={i.Id}>
      <button
        className="btn btn-secondary btn-block"
        onClick={() => onSelectCategory(i.Id)}
      >
        {i.CategoryName}
      </button>
      <br />
    </div>
  ));
  return <div>{links}</div>;
};

var ProductList = ({ products, selectedCategory, handleShow }) => {
  var count = 1;
  const { openModal } = this.state;
  const currentProducts = products
    .filter((i) => i.CategoryId === selectedCategory.Id)
    .map((i) => (
      <tr key={i.ProductId}>
        <td>{count++}</td>
        <td>{i.ProductName}</td>
        <td>{i.CategoryName}</td>
        <td>{i.SkuName}</td>
        <td>{i.Attributes}</td>
        <td>
          <Router>
            <button
              className="btn btn-info py-0 btn-sm"
              onClick={() => handleShow()}
            >
              Edit
              {openModal ? <EditProduct /> : null}
            </button>
            |
            <button
              className="btn btn-primary py-0 btn-sm"
              onClick={() => {
                this.setState({ setShow: true });
              }}
            >
              Details
            </button>{' '}
            |
            <button
              className="btn btn-danger py-0 btn-sm"
              onClick={() => {
                alert(i.ProductId);
              }}
            >
              Delete
            </button>
          </Router>
        </td>
      </tr>
    ));
  return (
    <div>
      <table class="table table-striped table-sm table-success table-bordered">
        <tbody>
          <tr>
            <th></th>
            <th>Product Name</th>
            <th>Category Name</th>
            <th>SKU</th>
            <th>Attributes</th>
            <th></th>
          </tr>
          {currentProducts}
        </tbody>
      </table>
    </div>
  );
};

export default Nav;
