import React, { Component } from 'react';
import http from './AppConfig';
import NavigationSubCategoryList from './NavigationSubCategoryList';
import NavigationCategoryList from './NavigationCategoryList';

const CATEGORIES_ENDPOINT = '/productCategories';
const PRODUCTS_ENDPOINT = '/products';

class NavContainer extends Component {
  getInitialState() {
    return {
      categories: [],
      items: [],
      selectedCategoryId: null,
    };
  }
  selectCategory(category) {
    this.setState({
      selectedCategoryId: category,
    });
  }
  componentDidMount() {
    this.serverRequest = http
      .all([http.get(CATEGORIES_ENDPOINT), http.get(PRODUCTS_ENDPOINT)])
      .then(
        http.spread((categoriesResponse, itemsResponse) => {
          console.log('Categories', categoriesResponse);
          console.log('Item', itemsResponse);

          // This `this` should work due to ES6 arrow functions
          this.setState({
            categories: [
              {
                ProductId: 1,
                ProductName: 't-Shirt',
                SkuId: 1,
                Price: 123.45,
                CategoryId: 1,
                CategoryName: 'Standard Products',
                SkuName: 'Pieces',
                Attributes: 'L,White,Extra Mandatory',
              },
              {
                ProductId: 2,
                ProductName: 'Vest',
                SkuId: 1,
                Price: 100.45,
                CategoryId: 1,
                CategoryName: 'Standard Products',
                SkuName: 'Pieces',
                Attributes: 'Black',
              },
            ],
            items: [
              { Id: 1, CategoryName: 'Standard Products' },
              { Id: 2, CategoryName: 'Products With Variants' },
              { Id: 3, CategoryName: 'Composite Products' },
            ],
          });
        }),
      );
  }
  componentWillUnmount() {
    this.serverRequest.abort();
  }

  render() {
    const { categories, items, selectedCategoryId } = this.state;

    return (
      <div className="navigation">
        <h1>Store Cupboard</h1>

        <NavigationCategoryList
          categories={categories}
          // Pass the select function into the category list
          // so the category items can call it when clicked
          selectCategory={this.selectCategory}
        />

        <NavigationSubCategoryList
          items={items}
          // Pass the selected category into the list of items
          // to be used for filtering the list
          selectedCategoryId={selectedCategoryId}
        />
      </div>
    );
  }
}
export default NavContainer;
