import { add, findLastKey } from 'lodash';
import React, { Component } from 'react';
import { Row, Form, Col, Button, Dropdown } from 'react-bootstrap';
import './Modal.css';
export default class EditProduct extends Component {
  constructor(props) {
    super(props);

    this.initialState = {
      ProductId: '',
      ProductName: '',
      CategoryId: '',
      SkuId: '',
      Price: '',
      Attributes: [],
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleAttrSelect = this.handleAttrSelect.bind(this);
  }
  componentDidMount() {}
  componentDidUpdate() {}
  handleChange(event) {
    const name = event.target.name;
    const value = event.target.value;

    this.setState({
      [name]: value,
    });
  }

  handleAttrSelect = (event) => {
    const attributeIds = [this.props.attributes];
    var attrId = [];
    attributeIds.map((a) => attrId.push(a.Id));
    this.state.checked = !this.state.checked;
    const items = this.state.selectedAttrs;

    if (this.state.checked) {
      items.push(event.target.value);
      this.state.selectedAttrs = items;
    } else {
      this.state.selectedAttrs.splice(items.indexOf(event.target.value), 1);
      this.state.selectedAttrs = items;
    }
    console.log(...this.state.selectedAttrs);
    /*var attrArr = [...this.state.attrs];
    const value = event.target.value;
    //alert(value);

    const index = attrArr.findIndex((att) => att === value);
    if (index > -1) {
      attrArr = [...attrArr.slice(0, index), ...attrArr.slice(index + 1)];
    } else {
      attrArr.push(value);
    }

    attrArr.push(value);
    this.setState({ attrs: attrArr }, () => {
      console.log(...attributeIds);
    });*/
  };

  handleSubmit(event) {
    event.preventDefault();
    this.props.onFormSubmit(this.state.prod);
    this.setState(this.initialState);
  }
  onClose = (e) => {
    this.props.handleClose && this.props.handleClose(e);
  };
  render() {
    if (!this.props.showEdit) {
      return null;
    }
    return (
      <div className="mod">
        <div className="mod-content">
          <div className="mod-header">
            <h4 className="mod-title">Edit Product</h4>
          </div>
          <div className="mod-body">
            <div className="card bg-warning">
              <div className="table-responsive">
                <Form onSubmit={this.handleSubmit}>
                  <table
                    className="table table-striped table-sm table-success table-bordered"
                    data-link="row"
                  >
                    <tbody>
                      <tr>
                        <td className="align-bottom">
                          <Form.Group controlId="ProductName">
                            <Form.Label>ProductName</Form.Label>
                            <Form.Control
                              type="text"
                              name="ProductName"
                              value={this.props.productData.ProductName}
                              onChange={this.handleChange}
                              placeholder="Product Name"
                            />
                          </Form.Group>
                          <Form.Group controlId="CategoryId">
                            <Form.Label>Category</Form.Label>
                            <Dropdown>
                              <Dropdown.Toggle
                                variant="success"
                                id="CategoryId"
                                name="CategoryId"
                                onChange={this.handleChange}
                                value={this.props.productData.CategoryId}
                              >
                                Category
                              </Dropdown.Toggle>
                              {this.props.categories.map((cat) => (
                                <Dropdown.Menu>
                                  <Dropdown.Item key={cat.Id} value={cat.Id}>
                                    {cat.CategoryName}
                                  </Dropdown.Item>
                                </Dropdown.Menu>
                              ))}
                            </Dropdown>
                          </Form.Group>
                          <Form.Group controlId="SkuId">
                            <Form.Label>SKU</Form.Label>
                            <Dropdown>
                              <Dropdown.Toggle
                                variant="success"
                                id="SkuId"
                                name="SkuId"
                                onChange={this.handleChange}
                                value={this.props.skus.SkuId}
                              >
                                Sku
                              </Dropdown.Toggle>
                              {this.props.skus.map((sku) => (
                                <Dropdown.Menu>
                                  <Dropdown.Item key={sku.Id} value={sku.Id}>
                                    {sku.SkuName}
                                  </Dropdown.Item>
                                </Dropdown.Menu>
                              ))}
                            </Dropdown>
                          </Form.Group>
                          <Form.Group controlId="Price">
                            <Form.Label>Price</Form.Label>
                            <Form.Control
                              type="text"
                              name="Price"
                              value={this.props.productData.Price}
                              onChange={this.handleChange}
                              placeholder="Price"
                            />
                          </Form.Group>
                        </td>
                      </tr>

                      <tr>
                        <td
                          colSpan="2"
                          className="align-bottom font-weight-bold"
                        >
                          <fieldset className="border p-2">
                            <legend className="w-auto">
                              Product Attributes
                            </legend>
                            {this.state.attributesData.map((attr) => (
                              <div key={attr.ProductAttributeValueId}>
                                <input
                                  type="checkbox"
                                  name={attr.ProductAttributeValueId}
                                  id={attr.ProductAttributeValueId}
                                  value={attr.ProductAttributeValueId}
                                  checked={attr.Selected}
                                  onChange={this.handleAttrSelect}
                                />
                                &nbsp;
                                {attr.AttributeName}
                              </div>
                            ))}
                          </fieldset>
                        </td>
                      </tr>
                      <tr>
                        <td colSpan="2">
                          <button
                            className="btn btn-sm btn-primary fa-pull-right"
                            type="submit"
                          >
                            Update Product
                          </button>
                          <button
                            className="btn btn-sm btn-danger"
                            onClick={this.onClose}
                          >
                            Close
                          </button>
                        </td>
                      </tr>
                    </tbody>
                  </table>
                </Form>
              </div>
            </div>
          </div>
          <div className="mod-footer"></div>
        </div>
      </div>
    );
  }
}
