import React, { Component } from 'react';
import { Link, BrowserRouter as Router } from 'react-router-dom';
import http from './AppConfig';
const _ = require('lodash');
const CATEGORIES_ENDPOINT = '/productCategories';
const PRODUCTS_ENDPOINT = '/products';

class Nav extends Component {
  constructor() {
    super();

    this.state = {
      categories: [],
      products: [],
      selectedCategoryId: null,
    };

    this.onSelectCategory = this.onSelectCategory.bind(this);
    this.editProduct = this.editProduct.bind(this);
  }
  componentDidMount() {
    http
      .get(CATEGORIES_ENDPOINT)
      .then((response) => response.data)
      .then(
        (result) => {
          this.setState({
            categories: result,
          });
        },
        (error) => {
          this.setState({ error });
        },
      );
    http
      .get(PRODUCTS_ENDPOINT)
      .then((response) => response.data)
      .then(
        (result) => {
          this.setState({
            products: result,
          });
        },
        (error) => {
          this.setState({ error });
        },
      );
  }
  onSelectCategory(Id) {
    this.setState({
      selectedCategoryId: Id,
    });
  }
  editProduct(Id) {
    alert(Id);
    this.setState({
      //selectedCategoryId: Id,
    });
  }

  render() {
    const { categories, products, selectedCategoryId } = this.state;
    const defaultCategory = _.first(categories);
    const selectedCategory =
      _.find(categories, (i) => i.Id === selectedCategoryId) || defaultCategory;
    return (
      <div className="row">
        <div className="col-sm-3">
          <CategoryFilter
            categories={categories}
            onSelectCategory={this.onSelectCategory}
          />
        </div>
        <div className="col-sm-9">
          <ProductList
            products={products}
            selectedCategory={selectedCategory}
          />
        </div>
      </div>
    );
  }
}

var CategoryFilter = ({ categories, onSelectCategory }) => {
  const links = categories.map((i) => (
    <div key={i.Id}>
      <Link
        className="btn btn-secondary btn-block"
        onClick={() => onSelectCategory(i.Id)}
      >
        {i.CategoryName}
      </Link>
      <br />
    </div>
  ));
  return (
    <div>
      <Router>{links}</Router>
    </div>
  );
};

var ProductList = ({ products, selectedCategory, editProduct }) => {
  var count = 1;
  const currentProducts = products
    .filter((i) => i.CategoryId === selectedCategory.Id)
    .map((i) => (
      <tr key={i.ProductId}>
        <td>{count++}</td>
        <td>{i.ProductName}</td>
        <td>{i.CategoryName}</td>
        <td>{i.SkuName}</td>
        <td>{i.Attributes}</td>
        <td>
          <button
            className="btn btn-info py-0 btn-sm"
            onClick={editProduct(i.ProductId)}
          >
            Edit
          </button>
          |<button className="btn btn-primary py-0 btn-sm">Details</button> |
          <button className="btn btn-danger py-0 btn-sm">Delete</button>
        </td>
      </tr>
    ));
  return (
    <div>
      <table class="table table-striped table-sm table-success table-bordered">
        <thead>
          <th></th>
          <th>Product Name</th>
          <th>Category Name</th>
          <th>SKU</th>
          <th>Attributes</th>
          <th></th>
        </thead>
        <tbody>{currentProducts}</tbody>
      </table>
    </div>
  );
};

export default Nav;
