import { add, findLastKey } from 'lodash';
import React, { Component } from 'react';
import { Row, Form, Col, Button } from 'react-bootstrap';
import './Modal.css';
export default class EditProduct extends Component {
  constructor(props) {
    super(props);

    this.initialState = {
      ProductId: '',
      ProductName: '',
      CategoryId: '',
      SkuId: '',
      Price: '',
      Attributes: [],
    };
    this.state = {
      attrs: [],
      prod: [],
      checked: true,
      selectedAttrs: [],
      attributesData: [],
    };
    this.setState((state, props) => ({
      attrs: [],
      prod: this.props.productData,
      checked: true,
      selectedAttrs: [],
      attributesData: this.props.attributesData,
    }));
    //this.state = this.props.productData;
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleAttrSelect = this.handleAttrSelect.bind(this);
  }
  componentDidMount() {}
  componentDidUpdate() {}
  handleChange(event) {
    const name = event.target.name;
    const value = event.target.value;

    this.setState({
      [name]: value,
    });
  }

  handleAttrSelect = (event) => {
    const attributeIds = [this.props.attributes];
    var attrId = [];
    attributeIds.map((a) => attrId.push(a.Id));
    this.state.checked = !this.state.checked;
    const items = this.state.selectedAttrs;

    if (this.state.checked) {
      items.push(event.target.value);
      this.state.selectedAttrs = items;
    } else {
      this.state.selectedAttrs.splice(items.indexOf(event.target.value), 1);
      this.state.selectedAttrs = items;
    }
    console.log(...this.state.selectedAttrs);
    /*var attrArr = [...this.state.attrs];
    const value = event.target.value;
    //alert(value);

    const index = attrArr.findIndex((att) => att === value);
    if (index > -1) {
      attrArr = [...attrArr.slice(0, index), ...attrArr.slice(index + 1)];
    } else {
      attrArr.push(value);
    }

    attrArr.push(value);
    this.setState({ attrs: attrArr }, () => {
      console.log(...attributeIds);
    });*/
  };

  handleSubmit(event) {
    event.preventDefault();
    this.props.onFormSubmit(this.state.prod);
    this.setState(this.initialState);
  }
  onClose = (e) => {
    this.props.handleClose && this.props.handleClose(e);
  };
  render() {
    if (!this.props.showEdit) {
      return null;
    }
    return (
      <div className="mod">
        <div className="mod-content">
          <div className="mod-header">
            <h4 className="mod-title">Edit Product</h4>
          </div>
          <div className="mod-body">
            <div className="card bg-warning">
              <div className="table-responsive">
                <form onSubmit={this.handleSubmit}>
                  <table
                    className="table table-striped table-sm table-success table-bordered"
                    data-link="row"
                  >
                    <tbody>
                      <tr>
                        <td className="align-bottom font-weight-bold">
                          <label className="control-label">Product Name</label>
                        </td>
                        <td className="align-bottom">
                          <input
                            type="text"
                            name="ProductName"
                            id="ProductName"
                            onChange={this.handleChange}
                            placeholder="Product Name"
                            className="form-control"
                            value={this.state.prod.ProductName}
                          />
                        </td>
                      </tr>
                      <tr>
                        <td className="align-bottom font-weight-bold">
                          <label className="control-label">Category</label>
                        </td>
                        <td className="align-bottom">
                          <select
                            id="CategoryId"
                            name="CategoryId"
                            className="form-control"
                            onChange={this.handleChange}
                            value={this.state.prod.CategoryId}
                          >
                            <option value="">====Select====</option>
                            {this.props.categories.map((cat) => (
                              <option key={cat.Id} value={cat.Id}>
                                {cat.CategoryName}
                              </option>
                            ))}
                          </select>
                        </td>
                      </tr>
                      <tr>
                        <td className="align-bottom font-weight-bold">
                          <label className="control-label">SKU</label>
                        </td>
                        <td className="align-bottom">
                          <select
                            id="SkuId"
                            name="SkuId"
                            className="form-control"
                            onChange={this.handleChange}
                            value={this.state.prod.SkuId}
                          >
                            <option value="">====Select====</option>
                            {this.props.skus.map((sku) => (
                              <option key={sku.Id} value={sku.Id}>
                                {sku.SkuName}
                              </option>
                            ))}
                          </select>
                        </td>
                      </tr>
                      <tr>
                        <td className="align-bottom font-weight-bold">
                          <label className="control-label">Price</label>
                        </td>
                        <td className="align-bottom">
                          <input
                            type="text"
                            name="Price"
                            id="Price"
                            className="form-control"
                            onChange={this.handleChange}
                            placeholder="Price"
                            value={this.state.prod.Price}
                          />
                          <input
                            type="hidden"
                            name="Id"
                            id="Id"
                            value={this.state.prod.ProductId}
                            onChange={this.handleChange}
                          />
                        </td>
                      </tr>
                      <tr>
                        <td
                          colSpan="2"
                          className="align-bottom font-weight-bold"
                        >
                          <fieldset className="border p-2">
                            <legend className="w-auto">
                              Product Attributes
                            </legend>
                            {this.state.attributesData.map((attr) => (
                              <div key={attr.ProductAttributeValueId}>
                                <input
                                  type="checkbox"
                                  name={attr.ProductAttributeValueId}
                                  id={attr.ProductAttributeValueId}
                                  value={attr.ProductAttributeValueId}
                                  checked={attr.Selected}
                                  onChange={this.handleAttrSelect}
                                />
                                &nbsp;
                                {attr.AttributeName}
                              </div>
                            ))}
                          </fieldset>
                        </td>
                      </tr>
                      <tr>
                        <td colSpan="2">
                          <button
                            className="btn btn-sm btn-primary fa-pull-right"
                            type="submit"
                          >
                            Update Product
                          </button>
                          <button
                            className="btn btn-sm btn-danger"
                            onClick={this.onClose}
                          >
                            Close
                          </button>
                        </td>
                      </tr>
                    </tbody>
                  </table>
                </form>
              </div>
            </div>
          </div>
          <div className="mod-footer"></div>
        </div>
      </div>
    );
  }
}
