import React, { Component } from 'react';
import { BrowserRouter as Router, Link } from 'react-router-dom';
import http from './AppConfig';
import url from './AppConfig';
export class CategoryList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      categories: [],
      response: {},
    };
  }
  componentDidMount() {
    http
      .get('/productCategories')
      .then((response) => response.data)
      .then(
        (result) => {
          this.setState({
            categories: result,
          });
        },
        (error) => {
          this.setState({ error });
        },
      );
  }

  render() {
    const { categories } = this.state;
    const catUrl = '/products';
    return (
      <Router>
        <div>
          <Link className="btn btn-secondary btn-block" to={url}>
            All
          </Link>
          {categories &&
            categories.map((cat) => (
              <Link
                className="btn btn-secondary btn-block"
                key={cat.Id}
                onClick={() => this.props.fp(cat.Id)}
                to={`${catUrl}/${cat.Id}`}
              >
                {cat.CategoryName}
              </Link>
            ))}
        </div>
      </Router>
    );
  }
}
