import React, { Component } from 'react';
import { Route, Switch, BrowserRouter as Router, Link } from 'react-router-dom';
import http from './AppConfig';
import url from './AppConfig';
import { ProductCategory } from './ProductCategory';
export class CategoryList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      categories: [],
      products: [],
      response: {},
    };
  }
  componentDidMount() {
    http
      .get('/productCategories')
      .then((response) => response.data)
      .then(
        (result) => {
          this.setState({
            categories: result,
          });
        },
        (error) => {
          this.setState({ error });
        },
      );
  }
  filterProduct = (Id) => {
    const { products } = this.state;
    http.get('/products/' + Id).then((result) => {
      this.setState({
        response: result,
        products: products.filter((product) => product.Id !== Id),
      });
    });
  };
  render() {
    const { categories } = this.state;
    const catUrl = '/products';
    return (
      <Router>
        <div className="row">
          <div className="col-sm-3">
            <div>
              <Link className="btn btn-secondary btn-block" to={url}>
                All
              </Link>
              {categories &&
                categories.map((cat) => (
                  <div>
                    <br />
                    <Link
                      className="btn btn-secondary btn-block"
                      onClick={() => this.filterProduct(cat.Id)}
                      key={cat.Id}
                      to={`${catUrl}/${cat.Id}`}
                    >
                      {cat.CategoryName}
                    </Link>
                  </div>
                ))}
            </div>
          </div>
          <div className="col-sm-9">
            <Switch>
              {categories.map((cat) => (
                <Route path={`${catUrl}/${cat.Id}`}>
                  <ProductCategory catId={cat.Id} />
                </Route>
              ))}
            </Switch>
          </div>
        </div>
      </Router>
    );
  }
}
