import React, { Component } from 'react';
import './Modal.css';
export default class AddProduct extends Component {
  constructor(props) {
    super(props);

    this.initialState = {
      Id: '',
      ProductName: '',
      CategoryId: '',
      SkuId: '',
      Price: '',
      Attributes: [],
    };

    if (this.props.productData.ProductId) {
      this.state = this.props.productData;
    } else {
      this.state = this.initialState;
    }

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(event) {
    const name = event.target.name;
    const value = event.target.value;

    this.setState({
      [name]: value,
    });
  }
  componentDidMount() {}
  handleSubmit(event) {
    event.preventDefault();
    this.props.onFormSubmit(this.state);
    this.setState(this.initialState);
  }
  onClose = (e) => {
    this.props.handleClose && this.props.handleClose(e);
  };
  render() {
    let pageTitle;
    let actionStatus;
    if (this.state.productData.ProductId) {
      pageTitle = <h2>Edit Product</h2>;
      actionStatus = <b>Update</b>;
    } else {
      pageTitle = <h2>Add Product</h2>;
      actionStatus = <b>Save</b>;
    }

    if (!this.props.show) {
      return null;
    }
    return (
      <div className="mod">
        <div className="mod-content">
          <div className="mod-header">
            <h4 className="mod-title">{pageTitle}</h4>
          </div>
          <div className="mod-body">
            <div className="card bg-warning">
              <div className="table-responsive">
                <form onSubmit={this.handleSubmit}>
                  <table
                    className="table table-striped table-sm table-success table-bordered"
                    data-link="row"
                  >
                    <tbody>
                      <tr>
                        <td className="align-bottom font-weight-bold">
                          <label className="control-label">Product Name</label>
                        </td>
                        <td className="align-bottom">
                          <input
                            type="text"
                            name="ProductName"
                            id="ProductName"
                            onChange={this.handleChange}
                            placeholder="Product Name"
                            className="form-control"
                            value={this.state.productData.ProductName}
                          />
                        </td>
                      </tr>
                      <tr>
                        <td className="align-bottom font-weight-bold">
                          <label className="control-label">Category</label>
                        </td>
                        <td className="align-bottom">
                          <select
                            id="CategoryId"
                            name="CategoryId"
                            className="form-control"
                            onChange={this.handleChange}
                            value={this.props.productData.CategoryId}
                          >
                            <option value="">====Select====</option>
                            {this.props.categories.map((cat) => (
                              <option key={cat.Id} value={cat.Id}>
                                {cat.CategoryName}
                              </option>
                            ))}
                          </select>
                        </td>
                      </tr>
                      <tr>
                        <td className="align-bottom font-weight-bold">
                          <label className="control-label">SKU</label>
                        </td>
                        <td className="align-bottom">
                          <select
                            id="SkuId"
                            name="SkuId"
                            className="form-control"
                            onChange={this.handleChange}
                            value={this.props.productData.SkuId}
                          >
                            <option value="">====Select====</option>
                            {this.props.skus.map((sku) => (
                              <option key={sku.Id} value={sku.Id}>
                                {sku.SkuName}
                              </option>
                            ))}
                          </select>
                        </td>
                      </tr>
                      <tr>
                        <td className="align-bottom font-weight-bold">
                          <label className="control-label">Price</label>
                        </td>
                        <td className="align-bottom">
                          <input
                            type="text"
                            name="Price"
                            id="Price"
                            className="form-control"
                            onChange={this.handleChange}
                            placeholder="Price"
                            value={this.props.productData.Price}
                          />
                          <input
                            type="hidden"
                            name="Id"
                            id="Id"
                            value={this.state.Id}
                            onChange={this.handleChange}
                          />
                        </td>
                      </tr>
                      <tr>
                        <td
                          colSpan="2"
                          className="align-bottom font-weight-bold"
                        >
                          <fieldset className="border p-2">
                            <legend className="w-auto">
                              Product Attributes
                            </legend>
                            {this.props.attributes.map((attr) => (
                              <div key={attr.Id}>
                                <input
                                  type="checkbox"
                                  name={attr.Id}
                                  id={attr.Id}
                                  value={attr.Id}
                                />
                                {attr.AttributeValue}
                              </div>
                            ))}
                          </fieldset>
                        </td>
                      </tr>
                      <tr>
                        <td colSpan="2">
                          <button
                            className="btn btn-sm btn-danger"
                            onClick={this.onClose}
                          >
                            Close
                          </button>
                          <button
                            className="btn btn-sm btn-primary fa-pull-right"
                            onClick={this.onClose}
                          >
                            {actionStatus}
                          </button>
                        </td>
                      </tr>
                    </tbody>
                  </table>
                </form>
              </div>
            </div>
          </div>
          <div className="mod-footer"></div>
        </div>
      </div>
    );
  }
}
