import React, { Component } from 'react';
import http from './AppConfig';
const _ = require('lodash');
const CATEGORIES_ENDPOINT = '/productCategories';
const PRODUCTS_ENDPOINT = '/products';

class Nav extends Component {
  constructor() {
    super();

    this.state = {
      categories: [],
      items: [],
      selectedCategoryId: null,
    };

    this.onSelectCategory = this.onSelectCategory.bind(this);
  }
  componentDidMount() {
    this.serverRequest = http
      .all([http.get(CATEGORIES_ENDPOINT), http.get(PRODUCTS_ENDPOINT)])
      .then(
        http.spread((categoriesResponse, itemsResponse) => {
          console.log('Categories', categoriesResponse);
          console.log('Item', itemsResponse);

          // This `this` should work due to ES6 arrow functions
          this.setState({
            categories: categoriesResponse,
            items: itemsResponse,
          });
        }),
      );
  }
  onSelectCategory(id) {
    this.setState({
      selectedCategoryId: id,
    });
  }

  render() {
    const { categories, items, selectedCategoryId } = this.state;
    const deafultCategory = _.first(categories);
    const selectedCategory =
      _.find(categories, (i) => i.id === selectedCategoryId) || deafultCategory;
    return (
      <div>
        <CategoryFilter
          categories={categories}
          onSelectCategory={this.onSelectCategory}
        />
        <ItemList items={items} selectedCategory={selectedCategory} />
      </div>
    );
  }
}

var CategoryFilter = ({ categories, onSelectCategory }) => {
  const links = categories.map((i) => (
    <div key={i.id}>
      <a href="#" onClick={() => onSelectCategory(i.id)}>
        {i.title}
      </a>
    </div>
  ));
  return <div>{links}</div>;
};

var ItemList = ({ items, selectedCategory }) => {
  const currentItems = items
    .filter((i) => i.category.id === selectedCategory.id)
    .map((i) => <div key={i.id}>{i.title}</div>);
  return <div>{currentItems}</div>;
};

export default Nav;
